css:
/***********************************************************************************
	KoGaMa Forums - Dark Theme (v1.0)
	Made by: Agreader3000
	- http://friends.kogama.com/profile/11052/
	Suggestions taken from: Rubix, Mero Mrmr Rero
	- http://friends.kogama.com/profile/30618/
	- http://www.kogama.com/profile/806112/
	Inpiration brought by: Youtube - Lights Out - A Dark Youtube Theme
	- https://userstyles.org/styles/92164/youtube-lights-out-a-dark-youtube-theme
	Made for: KoGaMa Forums
	- http://kogama.freeforums.net/
	
	Hope you enjoy! Keep your eyes safe, people! Don't burn 'em out!
************************************************************************************/



@-moz-document domain(kogama.freeforums.net) {
/*	Background	*/
body{
	background-color: rgb(10,10,10) !important;
	background-image: url(http://i.imgur.com/3yTpS3i.png) !important;
    background-repeat: round;
    color: white;
}

/*	Sections	*/
.container>.content, .content-box {
    background: #1b1b1b;
    border-color: #1b1b1b;
}
/*	SectionTable Border	*/
table, th, td, td>span {
   border: none !important;
   color: #aaaaaa;
}

/*	Boards	*/
table.list {
	color: #aaaaaa;
}

/*	Board:hover	*/
.item:hover, .link:hover {
    background-color: #1e1e1e !important;
    color: white !important;
}
.list .item:hover>.main .link a {
    color: white !important;
}

/*	Links	*/
.link a, .list a {
	color: #bbbbbb;
}

/*	Normal Members	*/
a.group-0, a.js-user-link.user-link {
	color: #87ffc8 !important;
}

/*	Moderators	*/
a.js-user-link.user-link.group-7 {
	color: #6ebee1 !important;
}

/*	Administrators	*/
a.js-user-link.user-link.group-1 {
	color: #a33535 !important;
}

/*	Section Titles	*/
.title-bar, #navigation-menu {
	background-color: #222222 !important;
    background-image: none !important;
}

/*	Timestamps	*/
.time {
	color: #7d7d7d !important;
}

/*	Navigation Bars	*/
#news, #navigation-tree, .recent-threads-button {
	background-color: #333333;
    color: white !important;
}

/*	Control Bar	*/
.control-bar, .ui-helper-clearfix {
    background-color: #333333 !important;
    border: 0 !important;
}

/*	Footer and Message	*/
#footer, .menu_arrow, #support-message, div#top-ad-banner {
	display: none !important;
}

/*	Links	*/
div>a>span, div>span, td#center-column>div>a {
	color: white !important;
}

/*	Banner Color	*/
#banner {
	background-color: #23272A !important;
    pointer-events: none;
}

/*	Images	*/
img {
	pointer-events: none;
}

/*	Current tab and Create Thread button	*/
.state-active, ul.controls>li>a {
	background: #2C2F33 !important;
}

/*	User List	*/
td.info>table>tbody>tr>td>a, td>div>div>a, td>div>a, h4>a, tr>td>a {
	color: #329682 !important;
}

/*	Control Box Buttons	*/
.ui-selectMenu-box, .search-input, td>input {
	background-color: #222222 !important;
    color: white !important;
}
.ui-pagination-slot>a, td>input, #options-container, .controls>a, .content-box>a {
	background-color: #222222 !important;
    color: white !important;
    border: 0 !important;
}
.ui-pagination-page.ui-pagination-slot.state-selected>a, .ui-pagination-next>a, .ui-pagination-prev>a, div.mini-user>a, form.form_post_quick_reply>input.ui-button, div.post-options, div.submit>span>input {
	background-color: #2C2F33 !important;
    color: white !important;
    border: 0 !important;
}

/*	Profile Page Stuff	*/
div.ui-tabMenu>ul.ui-helper-clearfix>li, div.visual-editor.ui-helper-clearfix, div.group.ui-helper-clearfix, div.editor.ui-helper-clearfix{
	background: #1b1b1b !important;
}
div.ui-tabMenu>ul.ui-helper-clearfix>li>a, .content.cap-bottom, div.content-box.center-col, .content-box>h4, div.mini-profile, div.mini-profile>div.info {
    color: white !important;
}
.ui-active>a {
	border: 0 !important;
}

/*	Friend Page Box	*/
div.micro-profile {
	background: #222222 !important;
    border: 1px solid black !important;
}

/*	Threads and Thread Creation	*/
table.list>tbody>tr.item {
	background: #1e1e1e !important;
}
table.list>tbody>tr.item.even {
	background: #282828 !important;
}
article>div.content-head.ui-helper-clearfix, tr.item>td.content>div.content-head {
	background: inherit !important;
}

div.message>textarea, code, div.bbcode-editor>textarea, div.quote_body {
	background: #23272A !important;
    color: white !important;
}
code, div.bbcode-editor>textarea {
    font-family: Consolas, monaco, monospace;
}
}
