css:
::-webkit-scrollbar{
	height: 20px;
	width: 20px;
	background: #f5f5f5;
	box-shadow: inset 0 0 7px 0px rgba(0, 0, 0, 0.1);
}
::-webkit-scrollbar-thumb{
	background: url(https://i.imgur.com/Ipb2zUU.png) #b3b2b2 center;
	background-size: 20px;
}
::-webkit-scrollbar-button:start {
	background: url(https://i.imgur.com/lFqA4tx.png) #b3b2b2 center no-repeat;
	background-size: 15px;
}
::-webkit-scrollbar-button:end {
	background: url(https://i.imgur.com/j8MSXXT.png) #b3b2b2 center no-repeat;
	background-size: 15px;
}
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-button:hover{
	background-color: #8e8e8e;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-button:active{
	background-color: #6f6f6f;
}
