css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reuters.com")

{

/*text&background*/

html, body, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer
{
color: white !important;
background: #333 !important;
}
  
tbody, padding-top, nav_white_space, nav ,header 
{
color: #333;
backgound: #333;
background-color: #333; 
}

select, button, div#masthead, div#dd-navigation, div.moduleBody, div#marketsHeader
{
background: #0 !important;
}

div, span, a
{
background-color: #333 !important;
}

select, input, span, code, textarea, pre, label, div, dd, option
{
color: #0 !important;
}


/*links*/

a
{
color: #c7c7c7 !important;
}

a:visited
{
color: #888 !important;
}

/*highlight*/

textarea, pre, input, code
{
background: #333 !important;
}

#breakingNewsContainer
{
  background: #333;
}
  
}
