css:
@-moz-document domain(twitter.com) {
  .ProfileAvatar,
  .avatar,
  .Avatar,
  .DMAvatar,
  .DashboardProfileCard-avatarImage,
  .DashboardProfileCard-avatarLink {
      clip-path: polygon(75% 7%, 100% 50%, 75% 93%, 25% 93%, 0 50%, 25% 7%);
      border-radius: 0;
  }

  .ProfileAvatar,
  .DashboardProfileCard-avatarImage {
      border: none;
  }
}
