css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bulbagarden.net") {

  /* Ad & cruft removal */
#bottomframe {display:none!important;}
#extadcontainer {display:none!important;}
#rightcolbox {display:none!important;}
#inhouseadcontainer {display:none!important;}
#headeradinhouse {display:none!important;}
#amazon-related {display:none!important;}
.ad-placement, .ad-tag, #headerad {display:none!important;}
#bottomframelower div div:first-child {display:none!important;}
#bottomframelower div div:nth-child(2) {display:none!important;}
#upper-article {display:none!important;}
div.ub-emb-container {display:none!important;}
#leaderboard_atf {display:none;}
div#mw-content-text~div.visualClear+div {display:none;}
div#cdm-zone-end {display:none;}
  /* Fix the content area */
#column-content, #content, #outercontentbox, #bodyContent {min-height:0px!important;}
#globalWrapper {float:right!important;width:85%!important;overflow:visible;box-sizing:border-box!important;}
body.page-Main_Page #globalWrapper {float:initial!important;width:initial!important;overflow:initial;}
#column-content {padding-right:15px!important;box-sizing:border-box!important;}
body.page-Main_Page #column-content {float:right!important;width:85%!important;overflow:visible;}
#content {max-width:100%!important;width:100%!important;float:left;border-top-right-radius:0px!important;border-bottom-right-radius:0px!important;border-right-width:0px!important;padding-right:0px!important;}
#contentbox {border-right-width:0px!important;}
body.page-Main_Page {margin-top:0px!important;}
html body {margin-top:0px!important;}
  /* Fix the menu */
#column-one {width:13%!important;box-sizing:border-box!important;display:block!important;visibility:visible!important;position:static!important;float:left!important;margin-left:16px!important;background-color:transparent;margin-top:61px;}
body.page-Main_Page #column-one {margin-top:initial;}
#column-one ::before {display:none!important;}
#column-one > div {margin-bottom:5px;border-style:solid;border-width:1px;border-color:#008800;background-color:#d8ffd8!important;border-radius:7px;}
#column-one > div ul {background-color:white;border-bottom-left-radius:7px;border-bottom-right-radius:7px;}
#column-one div li {height:11pt;}
#column-one div li:hover {background-color:#eeffee;}
#column-one div li:last-child:hover {border-bottom-left-radius:7px;border-bottom-right-radius:7px;}
#column-one div li a {font-size:8pt;vertical-align:middle;}
#column-one > div h3 {color:#006600;font-size:10pt;}
  /* Beautify the top navigation bar */
#navbarc {position:relative;left:-19%;width:119%;}
body.page-Main_Page #navbarc {left:initial;width:initial;}
#navbarc div {background-color:#eeffee!important;border-bottom-color:#008800!important;padding-left:1%;}
body.page-Main_Page #navbarc div {padding-left:initial;}
#navbarc div.header-box a span {color:#006600!important;}
#navbarc div.header-box a span:hover {color:#006600!important;text-shadow:0px 0px 2px #ffff00;}
#navbarc div#login-c div.reg-ac a {color:#006600!important;}
#navbarc #burger {display:none!important;}

}
