css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pythonbooks.org") {
  
  body {
    background-color: #282828;
    color: #ddd;
  }
  
  /*** Top bar ***/
  .navbar-default {
    background-color: #333333;
  }
  
  #mainNav {
    border-color: #666 !important;
  }
  
  /* Dropdown Menus */
  .dropdown-menu {
    background-color: #333333;
  }
  
  /*** Left-side Pane ***/
  .property {
    color: #aaa;
  }
  
  /* 'Reading Time' */
  .slider-track {
    background-color: #333333;
    background-image: none;
  }
  
  /* Separator */
  #filters {
    border-right-color: #666;
  }
  
  /*** Right-side Pane ***/
  
  /* 'ordered by popularity' */
  .nice-select {
    background-color: #282828;
    border-color: #666;
  }
  
  .nice-select .list {
    background-color: #282828;
  }
  
  .nice-select .option:hover, 
  .nice-select .option.focus, 
  .nice-select .option.selected.focus {
    background-color: #333333;
  }
  
  /** Book Row **/
  
  /* Author */
  .author {
    color: #aaa;
  }
  
  .book-wrapper-row {
    border-bottom-color: #666;
  }
  
  .bar svg > path[stroke="#FDCA98"] {
    stroke: #ddd !important;
  }
  
  /*** Footer ***/
  footer {
    background-color: #282828;
    border-top-color: #666;
  }
  
  #sign-up-form > input {
    border-color: #666;
  }
  
  /*** Main Page - http://pythonbooks.org ***/
  
  .category-column {
    background-color: #282828;
  }
  
  /*** Book Page ***/
  
  /* Description */
  #book-description-container {
    color: #ddd;
  }
  
  /* 'See Amazon Reviews', 'Comment on GitHub' buttons */
  .btn {
    background-color: #333333;
    border-color: #666;
  }
  
  .btn:hover, .btn:active, .btn:focus, .btn:active:focus {
    background-color: #666;
    border-color: #666;
  }
  

}
