css:
@-moz-document domain(weather.gov){
body {
  background-image:none !important; background-color: black;color:white;
}

.header{
  background-image:none !important; background-color: #4d4d4d;color:white;
}
.header-shadow{
  background-image:none !important; background-color: #262626;color:white;
}

.center{
  background-image:none !important; background-color: black;color:white;
}
.center-content{
  background-image:none !important; background-color: #4d4d4d;color:white;
}
.one-fourth-first{background-color: #262626;color:white;}
.communication-links{background-color: #262626;}

.footer{
  background-image:none !important; background-color: #262626;color:white;
}
.footer-legal{
  background-image:none !important; background-color: black ;color:white;
}
}

@-moz-document domain(forecast.weather.gov) {
    body{background-color:black;}

    main {background-color:#262626;color:white;}

    div .panel {background-color:#4d4d4d;color:white;}
    .panel-default>.panel-heading{background-image:none !important;background-color:black;color:white;}
    .row-odd {background-color:#262626;}
    .row-even {background-color:#4d4d4d;}
    .panel-danger>.panel-heading {
    color:#ebccd1;
        background-color: #330000;
        background-image: linear-gradient(to bottom,#330000 0,#660000 100%);}
    #sitemap{    background-color: #f7ffff;
        background-image: linear-gradient(to right,#1a1a1a,#000000);}
}


@-moz-document domain(radar.weather.gov){
body {
   background-image:none !important; background-color: black;
    
}
#main{color:white;}
#leftnav{
background-color:#0A2390;
}
}

@-moz-document domain(wpc.ncep.noaa.gov) {
body {
   background-image:none !important; background-color: black;
    
}
#main{color:white;}
#leftnav{
background-color:#0A2390;
}
}
