css:
@namespace url(http://www.w3.org/1999/xhtml);

/* http://userstyles.org/styles/145360 */
/* author: hg42 at gmx.net */

@-moz-document url("about:newtab") {
  
  #newtab-grid > .newtab-cell {
    /* height: 25ex; width: 30ex; */
    height: 15%;  width: 10%;
    margin-bottom: 0px;
  }
  
  #newtab-grid > .newtab-cell > .newtab-site * {
    font-size: 100%;
  }
  
  #newtab-grid {
    height: 100% !important;
    max-height: 100% !important;
    max-width: 100% !important;
    margin: 0 2ex 2ex 2ex !important;
    padding: 0;
    overflow: auto;
    /* background-color: hsla(240, 10%, 85%, 0.5); */
  }
  
  #newtab-grid, .newtab-cell {
    /* border: 1px solid gray; */
  }

  #newtab-search-container {
    background-image: url("chrome://branding/content/about-logo.png");
    background-repeat: no-repeat;
    background-size: 6ex;
    background-position: 0 0;
    margin: 2ex;
  }

  #newtab-margin-top, #newtab-margin-bottom, .newtab-side-margin {
    display:none;
  }
}
