/*/
 * ━━━━━━━━━━━━━━━━━━━━
 *   Simpler GooglePlus v5
 * ━━━━━━━━━━━━━━━━━━━━
 * 
 * 【Changelog】
 * 
 * Simpler GooglePlus v5 (2018/07/28[Sat])
 * > Google+のUI更新に伴うテーマ更新
 * > テーマ追加
 *   > サークルストリームに対応
 * > Css内にChangelogを追加
/*/



@-moz-document domain("plus.google.com"), domain("notifications.google.com") {
  /* ドロワー */
  Div.OFyC1e {
    Left: 1vw;
  }



  /* タブ */
  Div.OFyC1e A.n9qFx {
    Width: 48px;
    Height: 48px;

    Padding: 0;

    Border-Radius: 100%;
  }

  Div.OFyC1e A.n9qFx::After {
    Display: None;
  }

  /* タブ / タブ名 */
  Div.OFyC1e A.n9qFx Div.kiH7Kc {
    Display: None;
  }



  /* 第一タブ / アイコン */
  Div.OFyC1e A.n9qFx Div.OpEhZb,
  /* 第一タブ / サークルストリームタブ / ストリーム名 */
  Div.OFyC1e Div.ntes0e A.n9qFx Div.kiH7Kc {
    Display: Flex;
    Flex-Direction: Row;
    Justify-Content: Center;
    Align-Items: Center;

    Width: 48px;
    Padding: 0;
  }

  Div.OFyC1e A.n9qFx Div.OpEhZb > * {
    Margin-Top: 0;
  }

  /* 第一タブ / サークルストリームタブ / リスト */
  Div.OFyC1e Div.ntes0e.pIeyUb {
    Padding-Bottom: 0.5em;
    Border-Bottom: 1px Solid #DDDDDD;
    Margin-Bottom: 0.5em;
  }

  /* 第一タブ / サークルストリームタブ / アイコン */
  Div.OFyC1e Div.ntes0e A.n9qFx Div.OpEhZb {
    Display: None;
  }



  /* 第二タブ / リスト */
  Div.OFyC1e A.n9qFx.iVNuqf {
    Font: 24px "Material Icons Extended";
  }

  /* 第二タブ / アイコン */
  Div.OFyC1e A.n9qFx.iVNuqf::Before {
    Padding: 0.5em;
  }

  /* 第二タブ / 設定アイコン */
  Div.OFyC1e A.n9qFx.iVNuqf[Href="./settings"]::Before {
    Content: "settings";
  }

  /* 第二タブ / フィードバックアイコン */
  Div.OFyC1e A.n9qFx.iVNuqf[jsAction*="h5M12e"]::Before {
    Content: "feedback";
  }

  /* 第二タブ / ヘルプアイコン */
  Div.OFyC1e A.n9qFx.iVNuqf[Href^="https://support.google.com/"]::Before {
    Content: "help";
  }



  /* 第三タブ / リスト */
  Div.OFyC1e Div.AlZIPe {
    Display: None;
  }
}
