css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newmessage.org") {

#main, body, footer, .sf-menu ul,
.gray_line, .background-tile-1,
.the-message-intro-box-content,
.the-message-intro-box,
.the-message-books-volumes .message-book-div
{
background:#000000 !important;
}
.the-message-books-volumes h4.trigger,
.sub-menu li a:hover,
.widget_nav_menu a:hover, .widget_links a:hover, .widget_meta a:hover, .widget_nav_menu .current-menu-item > a
{
background:#222222 !important;
}
.the-message-intro-box-content,
.the-message-books-volumes .message-book-div
{
box-shadow:4px 4px 10px #0868CE !important;
}
.the-message-books-volumes h4.trigger
{
border:#222222 4px solid !important;
}
.the-message-books-volumes .message-book-div.more
{
border:#0868CE 3px solid !important;
}
.gray_line
{
border-top:#0868CE 1px solid !important;
}
h1, h2, h3, h4, h5, p, a, .text-color-gray-7,
.sf-menu ul a, .sub-menu li a,
footer .footer_additional .intro
{
color:#FFFFFF !important;
}
}
