css:
@-moz-document domain(tvtime.com) {
        #home, body, #shows-results, #calendar, #upcoming, #profile, #explore{
            background-color: #222 !important;
        }

        body a{
            color: #cbb123;
        }

        body a:hover{
            color: #ffee51;
        }

        .posters-list>li .poster-details h2 a {
            color: #CCC;
        }

        .fanarts-list>li .nav a:hover, .posters-list>li .nav a:hover {
            color: #CCC;
        }

        section h1{
            color: #CCC !important;
        }

        section h1 i{
            color: #f9c80c !important;   
        }

        .posters-list>li .nav a.followed {
            opacity: 1;
            color: #BBB !important;
            background-color: #444;
        }

        #profile .profile-nav{
            background-color: #999;
            border-bottom: 1px solid #333;
        }

        .nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
            background-color: #222;
        }

        #profile .profile-nav .nav-tabs>li.active, #profile .profile-nav .nav-tabs>li:focus, #profile .profile-nav .nav-tabs>li:hover {
            border-bottom: 2px solid #222;
        }

        #profile .profile-nav .nav-tabs>li.active .count, #profile .profile-nav .nav-tabs>li.active .label {
            color: #f9c80c !important;
        }

        .nav>li>a:focus, .nav>li>a:hover {
            background-color: #222;
        }

        #profile .profile-nav .nav-tabs>li:hover .label {
            color: #FFFC4E;
        }

        #profile .profile-nav .nav-tabs>li a{
            line-height: 21px;
        }

        #calendar .day .day-header .day-info .day-label {
            color: #fffc4e;
        }

        .page-sidebar {
            background-color: #333;
        }
}
