css:
@-moz-document url-prefix("https://duelingnexus.com/game"), url-prefix("https://duelingnexus.com/editor") {
h4, .h4 {
    font-size: 2vh;
}
#card-column {
    width: 25vw;
}
#card-name {
    font-size: 2vw;
    text-align: center;
    border: 1px dashed antiquewhite;
}
#card-picture {/*"%" represents what percentage of the #card-column this image file takes up.*/
    width: 55% /*45%*/;
}
#card-description-box {/*The cursor property is there for our Mac users, who's scrollbars are hidden unless in use. If you want a regular cursor, remove the "    cursor: s-resize;" line.*/
    font-size: 1.5vw;
    text-align: justify;
    border: 1px dashed antiquewhite;
    cursor: s-resize;
    overflow-x: hidden;
    overflow-y: scroll;
    height: 12.5vw !important;
}
.game-avatar-area {/*This ensures that the left-most username doesn't enter the #card-column.*/
    text-align: center;
    word-break: break-all;
    width: 81%;
}
/*Values in notes are the recommended changes for lower resolutions. However, they are only recommendations, so feel free to change this value to whatever suits your screen best.*/
}

@-moz-document url-prefix("https://duelingnexus.com/game") {
#game-chat-area {/*This is the box that contains the chat to a confined space. It's the heart of this Stylish.*/
    font-size: 0.9vw;
    border: 1px dashed antiquewhite;
    box-sizing: border-box;
    overflow-x: hidden;
    overflow-y: scroll;
    height: 9vw !important;
    width: 25vw;
    min-width: 0;
    margin: 0;
    padding: 0;
    position: fixed;
    bottom: 40px;
    left: 16px;
}
.engine-text-box {/*This is the smaller box where you type into.*/
    font-size: 0.9vw;
    border: 3px double #f0f0f0;
    box-sizing: border-box;
    height: 4vh;
    width: 21vw;
    margin: 0;
    padding: 0;
    position: fixed;
    bottom: 45px;
    left: 20px;
}
#game-column {
    width: auto;
}
#game-siding-column {
    height: 37.67vw;
    width: 33.33vw;
}
#siding-deck-main-title  {
	margin: 0 0 1vw 0;
}
#siding-deck-extra-title, #siding-deck-side-title {
	margin: 0.5vw 0;
}
#editor-main-deck {
    height: 18vw !important;
}
#editor-extra-deck, #editor-side-deck {
    height: 4.5vw !important;
}
#game-siding-column-2 {
    border: 1px dashed antiquewhite;
    height: 37.67vw;
    width: 37.67vw;
}
#game-siding-done {
    height: 4vw;
    width: 100%;
    position: relative;
    top: 5vw;
}

}

@-moz-document url-prefix("https://duelingnexus.com/editor") {
#editor-decks-column {
    height: 37.67vw;
    width: 33.33vw;
}
#editor-deck-main-title, #editor-search-title  {
	margin: 0 0 1vw 0;
}
#editor-deck-extra-title, #editor-deck-side-title {
	margin: 0.5vw 0;
}
#editor-main-deck {
    height: 18vw !important;
}
#editor-extra-deck, #editor-side-deck {
    height: 4.5vw !important;
}
#editor-search-column {
    border: 1px dashed antiquewhite;
    height: 37.67vw;
    width: 37.67vw;
}

}
