css:
@-moz-document domain("github.com") {
body {
    line-height: unset; }

.timeline-comment-header {
    padding-left: 7px;
    padding-right: 9px; }

.timeline-comment-header-text {
    padding-top: 1px;
    padding-bottom: 3px; }

.timeline-comment-action {
    padding: unset; }

.timeline-comment-wrapper > .timeline-comment::after,
.timeline-comment-wrapper > .timeline-comment::before,
.timeline-new-comment .timeline-comment::after,
.timeline-new-comment .timeline-comment::before {
    top: 5px; }

.comment-body {
    padding: 4px 8px 6px 8px; }

.reaction-summary-item {
    padding: 2px 4px 3px 2px;
    min-width: 60px; }

.reaction-summary-item > g-emoji {
    font-size: 14px;
    line-height: 16px;
    vertical-align: unset;
    margin-right: unset!important; }

.comment-reactions-options {
    opacity: 0.4; }

.comment-reactions.has-reactions:hover .comment-reactions-options {
    opacity: 1; }

.timeline-comment-label {
    padding: 0px 3px;
    margin: 2px 0 0 10px; }

.timeline-comment-wrapper {
    margin-top: 6px;
    margin-bottom: 6px; }

.dropdown-menu::before,
.dropdown-menu::after {
    content: none;
    display: none; }
}
