css:
@-moz-document domain(bluesnews.com) {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans Regular'), local('OpenSans-Regular'), url(https://fonts.gstatic.com/s/opensans/v14/cJZKeOuBrn4kERxqtaUH3bO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Bold'), local('OpenSans-Bold'), url(https://fonts.gstatic.com/s/opensans/v14/k3k702ZOKiLJc3WVjuplzOgdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
}

@media (max-width: 991px) {
    font[face="Arial, Helvetica"] {
        font-family: Open Sans, Arial, Helvetica, sans-serif !important;
    }
    #BluesNews-Main TD TD {
        font-size: larger;
        font-family: Open Sans, Arial, Helvetica, sans-serif !important;
    }
    #BluesNews-Two TD TD {
        font-size: larger;
        font-family: Open Sans,Arial, Helvetica, sans-serif !important;
    }
    #left-menu,
    #BluesNews-Two > tbody > tr > td:first-child {
        display: none;
    }
    span.day-header-text {
        font-size: 100% !important;
    }
    div.posting {
        padding-left: 30px;
        padding-right: 20px;
    }
    .posting,
    .posting table,
    .posting td,
    .posting li {
        font-size: larger !important;
        line-height: 1.5 !important;
        font-family:  Open Sans, Arial, Helvetica, sans-serif !important;
    }
    td.content table {
        display: block;
        height: auto;
        padding-bottom: 15px;
        padding-top: 15px;
    }
    td.content table caption {
        display: block;
    }
    td.content table thead {
        display: none;
        visibility: hidden;
    }
    td.content > table tbody,
    td.content > table tr,
    td.content > table th,
    td.content > table td {
        border: 0;
        display: block !important;
        padding: 0;
        text-align: left;
        white-space: normal;
    }
    td.content table tr {
        margin-bottom: 0;
    }
    /*    
  td.content table td
   {
	width:100%;
	}
*/
    
    td.content table td:empty {
        display: none;
    }
}
}
