css:
/* STYLE BY ADALEIGHFAITH || TUMBLR & INSTAGRAM */

@-moz-document domain("adf.ly") {

#interstitual, #body, div#please_wait {
background-color: #000000 !important;
color: #444444 !important;
}

#cookie_notice {
background-color: #181717 !important;
color: #444444 !important;
}

#a, #cookie_notice a, .sm_content a {
color: #9043cc !important;
}

#a:hover, #cookie_notice a:hover, .sm_content a:hover {
color: #be7fef !important;
}

.sm_content {
background-color: #181717 !important;
border: 4px solid #9043cc !important;
color: #444444 !important;
}

button#abC {
background-color: #9043cc !important;
}

#CashSlideDiv, #interstitual table tbody tr:not([height*="1%"]), #sitebar #bottom, #sitebar #bottomAd, #skip_ad_button, #top span:first-child, div[style*="display: inline-block; text-align: center; padding-top: 20px; height: 20px; width: 60%; color: #ffffff; letter-spacing: 2px"], img#bee {
display: none !important;
}

#interstitual #top {
background-image: none;
position: relative !important;
}

#interstitual table tbody tr td {
position: absolute;
top: 30%;
}

#please_wait {
margin-left: -155px;
top: 301px !important;
}

#skip_button {
background-image: url("http://i.imgur.com/nufkEfH.png");
height: 41px !important;
right: auto !important;
top: 301px !important;
width: 155px !important;
}

#top span {
left: 50%;
margin-left: -77.5px;
position: absolute !important;
width: 155px !important;
}

}
