css:
@-moz-document domain("rubydoc.info") {
html {
    background-color: #333;
    color: #eee;
}
#navigation li a {
    background-color: #222;
    border: 1px solid #444;
}
#navigation li.selected {
	background-color: #111;
    border: 1px solid #444;
}

ul.alpha_index li a{
	background-color: #222;
    border: 1px solid #444;
}

.alpha_index li.selected{
    background-color: #111;
    border: 1px solid #444;
}
.libraries li {
	border-bottom: 1px solid #444;    
}
ul.libraries li.r1, 
li.odd,
.box_info dl:nth-child(odd) > *
{
    background-color: #212121;
}
ul.libraries li.r2, 
li.even,
.box_info dl:nth-child(even) > *
{
   background-color: #121212;
}

.box_info dl:nth-child(odd) > *,
.box_info dl:nth-child(even) > *
{
 border-color: #555;   
}

.fixed_header, #main {
     background-color: #333;
}

#menu a {
    color: #bcbcbc;
}

#menu .title {
	color: #cecece;
}

#menu a:hover {
 	color: white;   
}

p.signature, h3.signature, 
.summary .summary_signature,
h2 small a
{
    background-color: #222;
    border: 1px solid #444;
}
h2 {
       border-bottom: 1px solid #666;
}
#content a, #content a:visited {
    color: #3aa0ec;
}
.note.private {
  background-color: #ded3a4;
    border-color: #ded3a4;
}
p.signature .extras, h3.signature .extras {
	color: #888;        
}
.item:hover, 
.libraries li:hover
{
 	background-color: #3a3a3a;   
}

#content a:hover {
 	background-color: transparent;
    border-bottom: 2px solid #666;
}
}
