css:
@-moz-document url-prefix("http://osu.gatari.pw/u/"), url-prefix("https://osu.gatari.pw/u/") {
.tab-content>.tab-pane {
    display: block!important;
}
.load-more-user-scores {
    margin-bottom: 20px!important;
}
#best-plays-table tr {
    border-bottom: solid 1px #eee!important;
    font-size: 15px!important;
}
#best-plays-table tr .text-right {
    white-space: nowrap!important
}
.play-info {
    font-size: 12px!important;
}
}

@-moz-document domain("osu.gatari.pw") {
* {
    font-family: Lato, 'Helvetica Neue', Arial, Helvetica, sans-serif;
}
body {
    background-image: url('http://osu.gatari.pw/images/page-light.png') !important;
    font-style: normal!important;
}
.col-lg-12 {
    background-image: none!important;
    background-color: #fff!important;
}
.pulse {
    animation-iteration-count: 0!important;
}
.navbar-inverse {
    background-image: url('http://i.imgur.com/W9d8FDy.png')!important;
}
.alert.alert-warning.margin {
    background-color: #555!important;
    background-image: url('http://i.imgur.com/W9d8FDy.png')!important;
}
}

@-moz-document url-prefix("http://osu.gatari.pw/leaderboard"), url-prefix("https://osu.gatari.pw/leaderboard") {
.table-striped tr {
    border-bottom: solid 1px rgba(200, 200, 200, 0.25)!important;
}
}

@-moz-document url-prefix("http://osu.gatari.pw/beatmaplist"), url-prefix("https://osu.gatari.pw/beatmaplist") {
.card.hovercard .cardheader {
    height: 100px!important
}
.corner-ribbon {
    top: 35px!important;
}
.card.hovercard .bottom {
    white-space: normal!important;
}
}
