css:
@-moz-document domain("quora.com") {
	.grid_page {
		margin: 0 auto !important;
		width: 100% !important;
	}
	.layout_3col_left, .layout_3col_right, .layout_2col_side {
		display:none;
	}
	.layout_3col_center, .layout_2col_main {
		padding: 0 !important;
		margin: 0 auto !important;
		width: 100% !important;
	}
	.modal_signup_background, .modal_signup_dialog, .dialog_wrapper {
		display:none !important;
	}
	* {
		font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
		font-size: 1em;
		line-height: 22px;
	}
}
