body {
    width: 100vw;
    height: 100vh;
}

.centered_planner {
    width: 100vw;
    height: 100vh;
    margin: 0;
}

#MainFrame {
    width: 100vw;
    height: 100vh;
}

#PlannerFrame {
    width: 100vw;
    height: 100vh;
}

#planer_frame {
    width: 100vw;
}

#main_menu_panel {
    height: auto !important;
    bottom: 20px !important;
}

#item_property_panel {
    left: auto !important;
    right: 20px;
}

#planner_frame #item_selector_panel {
    top: auto !important;
    bottom: 20px;
    right: 210px;
    width: auto;
}

#item_selector_scroll {
    width: auto !important;
}

#camera_control {
    top: auto !important;
    bottom: 215px;
    width: auto;
}

#player_panel {
    width: auto;
    height: auto;
    bottom: 260px;
    right: 210px;
}
}
