css:
@-moz-document domain(youtube.com)
{

.ytp-volume-slider {
transform: rotate(90deg);
}

#player-api, #placeholder-player {
transform: rotate(48deg);
    position: relative;
    left: 50px;
}

.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.like-button-renderer-like-button.like-button-renderer-like-button-unclicked.yt-uix-post-anchor.yt-uix-tooltip::after {
content: "5901581095097124187912575157189571896170925681296125185141";
}

.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.like-button-renderer-like-button.like-button-renderer-like-button-unclicked.yt-uix-post-anchor.yt-uix-tooltip {
z-index: 100000000000000000000000;
    position: relative;
    color: red;
}

.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.like-button-renderer-dislike-button.like-button-renderer-dislike-button-unclicked.yt-uix-post-anchor.yt-uix-tooltip::after {
content: ". Pretty bad, would not recommend >:(";
}

.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.like-button-renderer-dislike-button.like-button-renderer-dislike-button-unclicked.yt-uix-post-anchor.yt-uix-tooltip {
z-index: 100000000000000000000000;
    position: relative;
    color: green;
}

.ytp-right-controls {
padding: 10px;
}

.watch-stage-mode #theater-background {
background: linear-gradient(red, yellow);
}

.ytp-time-current::before {
content: "1059719471";
}

#watch7-content {
background-color: yellow;
}

.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
background: linear-gradient(red, yellow);
}

.ytp-load-progress {
background-color: green;
}

.ytp-progress-list {
background-color: black;
}

.yt-card {
background-color: yellow;
}

.yt-subscription-button-subscriber-count-branded-horizontal.yt-subscriber-count::before {
content: "0958195795810929571928529857928561986517109571984571095710957198571498619871";
    z-index: 10000000000000000;
    position: relative;
}

.yt-subscription-button-subscriber-count-branded-horizontal.yt-subscriber-count::after {
content: "NEGATIVE BILLION TRILLION SUBSCRIBERS";
}

.yt-subscription-button-subscriber-count-branded-horizontal.yt-subscriber-count {
    z-index: 10000000000000000;
    position: relative;
    background-color: green;
    color: red;
}

.subscribe-label::before {
content: "Un";
}

.subscribe-label {
text-transform: capitalize;
}

.watch-view-count::before {
content: "590127501279571096401956910967108969125";
    position: relative;
    z-index: 10000000000000000000;
}

#body {
background-image: url(http://image.ibb.co/iAOMQQ/whatthe.png);
}

#appbar-guide-menu {
transform: rotate(129deg);
    position: absolute;
    left: 100px;
    background-color: red;
    top: 100px;
}

#yt-masthead #logo-container .logo{
background: url(http://image.ibb.co/cvPLkQ/tolab.png);
    width: 125px;
}

#body {
font-family: comic sans ms;
    font-weight: bold;
}

#yt-masthead-container {
background-color: brown;
}

.comment-renderer-like-count.off::before {
content: "951909759285719285 ENGLAND IS MY CITY";
}

.hd-banner-image, #gh-banner  {
transform: rotate(57deg);
}

.ytp-iv-video-content {
transform: rotate(89deg);
}
  
}
