css:
@-moz-document domain("coliss.com"),
               domain("weble.org"),
               domain("veadardiary.blog29.fc2.com") {
  *.dummy { display: none !important; }
}

/* コリス */
@-moz-document domain("coliss.com") {

   #header
  ,#sidebar
  ,div[class*="google"]
  ,#trackback
  ,#leaveYourComments
  ,#comments
  ,#footer, #end
  ,.pageTopBar
  { display: none !important; }

  #container { width: 600px !important; }
  #container3 { margin: 0 auto !important; height:inherit !important;}
  #content h1 { padding: 2em 0 !important; }
  #content { margin: 2em 26px 0 !important; }
}

/* ウェブル */
@-moz-document domain("weble.org") {
    header>.right, #sidebar, .ads, *[class*="Ads"],
    .facebook, .comments, footer
    { display: none !important; }
    header, #content { width: 600px !important; }
}

/* Macの手書き説明書 */
@-moz-document domain("veadardiary.blog29.fc2.com") {

   #topmenu, #submenu ,#ps_top
  ,#navi
  ,#outbrain_widget_0
  ,div.entry>ins
  ,ddiv#blog>div:nth-child(4)
  ,.navi-title
  ,.zenback-entries
  /*,.zenback-links */
  ,.zenback-keywords>*
  ,.zenback-powered-topsy>*
  ,.zenback-powered>*
  ,#zenback-news>*
  ,#feedControl
  ,#comment-title
  ,#comment-title+div
  ,#tb_url
  ,div.pageselect
  ,#footer
  { display: none !important; }
  
  #blog { width: inherit !important; }
}

/* phpspot開発日誌 */
@-moz-document domain("phpspot.org") {
  #banner, #rightbar, #con_right, #article_navi,
  #con_left>h4+div[style], #socialbutton+div[style]
  { display: none !important; }
  #container { width: 560px !important; }
}
