css:
@-moz-document domain("sweepsdb.com") {
.nav-wrapper, .blue.darken-1, .page-footer, .blue, .footer-copyright {
    background-color: #161616 !important;
}

main , .modal-content{
    background-color: #3d3d3d !important;
}

.card-panel, .blue.lighten-5, .ta-body, .modal-footer {
    background-color: #5b5b5b !important;
    color: white !important;
}

.bordered.highlight.contesttable.sortable {
    color: #e4e4e4 !important;
}

.bordered.highlight.contesttable.sortable a {
    color: white !important;
    font-weight: bold;
}

.contestrow:hover {
    color: black !important;
}

.sponsored {
    background-color: #161616 !important;
}

#dropdownfilter, #dropdown3 {
    background-color: #5b5b5b !important;
    font-weight: bold;
}

.dropdown-content li > a {
    color: white !important;
}
}
