css:
@-moz-document domain("dev.to") {
body .top-bar {
	filter: invert(90%) !important;	
}

.universal-page-content-wrapper.stories-index,
.universal-page-content-wrapper.notifications-index,
.universal-page-content-wrapper.stories-search,
.universal-page-content-wrapper.podcast_episodes-index,
.home .articles-list,
.home .articles-list .single-article,
.home .articles-list a,
#page-content-inner,
.container.article,
#article-body,
.home .side-bar .sidebar-nav .sidebar-profile-snapshot {
	background-color: #242424 !important;
	color: #ddd !important;
}
#page-content-inner div.comments-container-container {
	background-color: #333 !important;
	color: #ddd !important;
	border-top:1px solid #eee;
}
/* .home .articles-list .article-engagement-count a,
#sidebar-additional .widget, */
#index-container .widget,
.comments-container .comment-trees .single-comment-node .inner-comment {
	background: #eee !important;
	color: #222 !important;
}
.comments-container .comment-trees .single-comment-node > .inner-comment {
  padding: 0px;
  border-left: 10px solid #51F7ED !important;
  border-radius: 3px;
}
.comments-container .comment-trees .single-comment-node > .single-comment-node .inner-comment{
  border-left: 10px solid #F1A5C0 !important;
}
.comments-container .comment-trees .single-comment-node > .single-comment-node .inner-comment:last-child {
  border-left: 10px solid #AFFEC7 !important;
}

.user-profile-header {
	background: #202020;
}
.notifications-index .home .articles-list .single-article .comment-text {
	display:none;
}
}
