css:
@-moz-document url-prefix('http://binsearch'), url-prefix('https://binsearch'), url-prefix('http://www.binsearch'), url-prefix('https://www.binsearch')
{ 

/*
* Author : BvD 
* Date   : 01/08/2017
* Browser: Chrome
* Version: 1.0
*
*   
*   
* 001 - General 
* 002 - results    
*/


/***************************/
/*      001 - General      */
/***************************/



:root
{
    --accent     : #ffb400;
    --bg         : #2d2d2d;
    --bgdark     : #232323;
    --border     : #424242;
    --txt        : #a3a3a3;
    --txtdark    : #767676;
    --txtlight   : #cecece;
    --bginput    : rgb(200,200,200);
    --bginputdark: rgb(175,175,175);
}

body
{
    background-color: var(--bg) !important;
    color: var(--txt) !important;
}
a
{
    color: var(--accent) !important;
    text-decoration: none !important;
}
a:hover
{
    text-decoration: underline !important;
}
hr
{
    border-color: var(--border) !important;
}
h2, h3
{
    color: var(--txtlight) !important;
}
input 
{
    padding: 2px 6px !important;
    font-size: 14px !important;
}


::selection
{
    color: black !important;
    background-color: var(--accent) !important;
}
:focus
{
    outline-color: var(--accent) !important;
}


::-webkit-scrollbar
{
    width: 12.5px !important;
    height: 10px !important;
    background: var(--bg) !important;
}
::-webkit-scrollbar-thumb
{
    background: var(--accent) !important;
    border: none !important;
    box-shadow: inset 0 0 2px 0 rgba(0,0,0,0.8) !important;
}
::-webkit-scrollbar-thumb:hover 
{
    box-shadow: inset 0 0 100px 0 rgba(0,0,0,0.5) !important;
}
::-webkit-scrollbar-track
{
    background: var(--bg) !important;
    box-shadow: inset 0 0 2px 1px rgba(0,0,0,0.8) !important;
}


/* textfields for the user */
input[type="text"], select
{
    background-color: var(--bginput) !important;
    border: 1px solid rgba(0,0,0,0.9) !important;
    box-shadow: inset 0 px 2px rgba(0,0,0,0.9) !important;
}
input[type="text"]:hover, select:hover
{
    background-color: var(--bginputdark) !important;
}

/* these all look like buttons */
input[type="button"], input[type="submit"]
{    
    background-color: rgba(255,255,255,0.5) !important;
    border: 1px solid rgba(0,0,0,0.9) !important;
    color: rgba(0,0,0,0.75) !important;
}
input[type="button"]:hover, input[type="submit"]:hover
{    
    background-color: rgba(255,255,255,0.35) !important;
}


/* textfields for the user - autofill */
input:-webkit-autofill
{
    background-color: red !important;
    -webkit-text-fill-color: black !important;
    -webkit-box-shadow: inset 0 0 0px 1000px var(--bginput) !important;
}


/* background for the header and footer */
.g
{
    background-color: var(--bgdark) !important;
    border-color: var(--border) !important;
}



/***************************/
/*      002 - Results      */
/***************************/



/* results - borders */
#r2
{
    border-color: transparent !important;
}
/* results - header */
.xMenuT th
{
    background-color: var(--accent) !important;
    color: rgba(0,0,0,0.5) !important;
}
/* results - rows - odd */
.xMenuT tr[bgcolor="#FFFFFF"]
{
    background-color: var(--bgdark) !important;
    color: var(--txt) !important;
}
/* results - rows - even */
.xMenuT tr[bgcolor="#F6F7Fa"]
{
    background-color: var(--bg) !important;
    color: var(--txt) !important;
}
/* results - rows - banner ("the posts below were posted a long time ago...") */
.xMenuT tr[bgcolor="#ffffbb"]
{
    background-color: var(--bg) !important;
    color: var(--txt) !important;
}
/* results - rows - odd / even */
.xMenuT tr:hover
{
    background-color: rgba(255,255,255,0.1) !important;
}
/* results - rows - number */
.xMenuT tr td:first-child
{
    text-align: center !important;
    color: var(--txtlight) !important;
}


}
