@namespace url(http://www.w3.org/1999/xhtml);
/*
* Author: Ew0345
* Version: 1.0
*/

/* AGENT_SHEET */
@-moz-document url-prefix("about:reader") {
  /*[[betterDarkMode]]*/
  /* Toolbar - Main */
  .toolbar,
  .toolbar #close-button,
  .toolbar .dropdown-toggle.button.style-button,
  .toolbar #narrate-toggle {
    background-color: #1a1a1a !important;
    border-color: #292929 !important;
  }
  .toolbar #close-button:hover,
  .toolbar .dropdown-toggle.button.style-button:hover,
  .toolbar #narrate-toggle:hover {
    background-color: #2d2d2d !important;
  }
  /* Toolbar Popups - Reader Settings */
  #reader-popup {
    background-color: #232323 !important;
  }
  #reader-popup div[id="font-type-buttons"] button div[class="name"] {
    color: #eee !important;
  }
  .sans-serif-button:hover,
  .serif-button:hover,
  .minus-button:hover,
  .plus-button:hover,
  .content-width-minus-button:hover,
  .content-width-plus-button:hover,
  .line-height-minus-button:hover,
  .line-height-plus-button:hover,
  .dark-button:hover {
    background-color: #2d2d2d !important;
  }
  .light-button:hover {
    background-color: #f0f0f0 !important;
  }
  .sepia-button:hover {
    background-color: #f0f0f0 !important;
  }
  /* Toolbar Popups - Narrate Settigns */
  #narrate-dropdown .dropdown-popup {
    background-color: #232323 !important;
  }
  #narrate-control button:hover {
    background-color: #2d2d2d !important;
  }
  #narrate-control button[disabled=""]:hover {
    background-color: transparent !important;
  }
  .select-toggle,
  .option {
    color: #eee !important;
  }
  .select-toggle:hover,
  .voice-options:hover,
  .option:hover {
    background-color: #2d2d2d !important;
  }
  
}
