css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("msnbc.com") {

     
/* For '.* /msnbc.com/.* /watch/.*' */     
   .pane-video-player, .tpPlayer, .tpContainer, .tpVideoBlocker {
       height: 389px !important;
       width: 761px !important;
       overflow: hidden !important;
  }
  
    .pane-video-player:hover {
       overflow: visible !important;
  }
     
/* For '.* /msnbc.com/.*' */
    .pane-show-player, .tpPlayer, .tpContainer, .tpVideoBlocker {
       height: 392px !important;
       width: 768px !important;
       overflow: hidden !important;
  }
  
    .pane-show-player:hover {
       overflow: visible !important;
  }  

}
