css:
@-moz-document url-prefix("http://localhost"), regexp("http://192\\.168\\..*") {
:root {
    --color_bg1: #1d1d1d;
    --color_bg2: #292929;
    --color_bg3: #363636;
    --color_bg4: #424242;
    --color_bg5: #4f4f4f;
    --color_bg6: #5c5c5c;
    --color_bg7: #696969;
    --color_bg8: #757575;
                                            
    --color_bg_accent1: #2d3336;
    --color_accent2: #ebd07f;
    --color_accent3: #7497ec;
    
    --color_fg1: #b3b3b3;
    --color_fg2: #bfbfbf;
    --color_fg3: #cccccc;
    --color_fg4: #d9d9d9;
    --color_fg5: #e5e5e5;
    --color_fg7: #fff;
}

html, body {
    height: 100%;
}

.page {
	min-height: 100%;
}

.pageinner {
	box-shadow: 0 0 16px rgba(0, 0, 0, 0.25);
    border-radius: 4px;
    border: 1px solid var(--color_bg3);
}

.page {
	background-color: var(--color_bg1);
}

.listingcontainer, .pageinner {
	background-color: var(--color_bg2);
}

.head2 {
	background-color: var(--color_bg_accent1);
    border-color: var(--color_bg4) !important;
    box-shadow: none;
}

table.listing th, table.listing td {
	border-color: var(--color_bg3);
    transition-duration: 0.1s;
}

table tr.tr_item {
    transition-duration: 0.1s;
}

table tr.tr_item:hover {
	background-color: var(--color_bg4);
}

table tr.tr_item:hover td, table tr.tr_item:hover th {
	border-color: var(--color_bg6);
}

table td a,
table.listing th, table.listing td {
	color: var(--color_fg2);
    transition-duration: 0.1s;
}

table td a:hover {
	color: var(--color_fg7);
}

.hilight td a {
    font-weight: bold;
    color: var(--color_accent3);
}

}
