css:
@-moz-document domain("desire2learn.com") {
.d2l-typography {
    color: #000000;
    display: block;
    font-family: Lato,'Lucida Sans Unicode','Lucida Grande',sans-serif;
    letter-spacing: .01rem;
    font-size: .95rem;
    font-weight: 400;
    line-height: 1.4rem;
}

a.d2l-navigation-s-link, a.d2l-navigation-s-link:link, a.d2l-navigation-s-link:visited {
    color: #000000;
    cursor: pointer;
    text-decoration: none;
}

.d2l-navigation-s-button-highlight {
    -ms-flex-align: center;
    align-items: center;
    background-color: rgba(153, 174, 229, 0);
    border: none;
    color: #565a5c;
    cursor: pointer;
    display: -ms-flexbox;
    display: flex;
    font-family: inherit;
    font-size: .7rem;
    height: 100%;
    margin: 0;
    overflow: visible;
    padding: 0;
    position: relative;
}

.d2l-navigation-s, .d2l-navigation-s-no-login {
    background-color: rgba(153, 174, 229, 0.15);
    line-height: 1;
    min-width: 320px;
    position: relative;
}

.d2l-navigation-s-divider d2l-icon {
    color: rgba(76, 87, 115, 1);
}

}
