css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vaderradar.se"), domain("vaerradar.no"), domain("bygeradar.dk"), domain("saatutka.fi") {

/* Remove gray bar on top */
  table[height="19"] { display: none !important; }
  div#Div1,#Div3,#Div4,#Div5 { display: none !important; }
  div#DivZoek { display: none !important; }
  div#languageBanner { display: none !important; } 

/* Remove side images */
  td[width="20"] { display: none !important; }
  td[background="images/balk_l_02.gif"] { display: none !important; }

/* Center page */
  #Div1 + table { width: auto !important; margin: 0px auto !important; }
  form[id="aspnetForm"] div + div > table { width: auto !important; margin: 0px auto !important; }

/* Remove footer */
  td[colspan="3"] { display: none !important; }
  form[id="aspnetForm"] div + div > p { display: none !important; }
  form[id="aspnetForm"] div + div > table > tbody > tr > td + td > p { display: none !important; }
  form[id="aspnetForm"] div + div > table > tbody > tr > td + td > a + a { display: none !important; }
  img[src="images/footer.gif"] { display: none !important; }

/* Remove dot-images */
  img[src="images/rm.gif"] { display: none !important; }
  img[src="images/stip2.gif"] { display: none !important; }
  img[src="images/stip.gif"] { display: none !important; }

/* Remove banners */
  iframe[name="google_ads_frame"] { display: none !important; }
  td[width="120"] { display: none !important; }
  span[id="ctl00_ContentPlaceHolder1_updatePanel1"] > table + table > tbody > tr > td > div > table > tbody tr + tr { display: none !important; } 
  span[id="ctl00_ContentPlaceHolder1_updatePanel1"] > table + table > tbody > tr > td + td + td { display: none !important; }
  div[id="banner"]{ display: none !important; }
  a[href="http://www.klacken.com/"] { display: none !important; }

/* Resizen banner on top of page */
  a[target="_top"][href="/"], a[target="_top"][href="http://www.meteox.com"], a[target="_top"][href="http://www.meteox.co.uk"], a[target="_top"][href="http://www.meteox.fr"] {
    width: 960px !important;
    overflow: hidden !important;
    display: block !important;
    margin-left: auto !important; 
    margin-right: auto !important; 
    margin: 0px 0px 0 -5px !important;
  }

/* Menubalk links uitlijning wijzigen */
  td[width="110"][align="right"] { text-align: left !important; }
  #Div1 + table td[width="900"] > table > tbody > tr + tr > td + td > table > tbody > tr > td[align="left"]  { display: none !important; }
}
