css:
@-moz-document regexp("http://www\\.ttriju\\.com/.*")
{
#cs_right_bottom {
	display: none;
}
.tag-box table tr {
	border-color: #233 !important;
}
.pager-bg {
	background: #666 !important;
}
.announcement {
	display: none;
}
.tag-box table tr td {
	border-top: 2px solid #233 !important;
}
.movie-item-in .meta {
	background-color: #233 !important;
}
.movie-item-in .meta h1 a,
.movie-item-in .meta h4 a {
	color: #fff !important;
}
a.tag {
	background-color: rgba(150, 32, 41, 0.9) !important;
	color: #fff !important;
}
.movie-item-in .meta .otherinfo a {
	background-color: rgba(100, 32, 41, 0.9) !important;
	color: #ddd !important;
}
.tag-span2 {
	color: #ddd;
}
object,
.movie-title,
footer {
	display: none;
}
.movie-title {
	text-align: center;
}
.navbar,
header,
body,
.container,
.container-fluid,
.breadcrumb {
	background: #000 !important;
}
button {
	border-color: #aaa !important;
	content: "" !important;
}
.form-group input {
	width: 150px !important;
	background: #000 !important;
}
.form-group input::-webkit-input-placeholder {
	color: #000;
}
.form-group input {
	color: #fff;
	border-color: #aaa;
}
header div {
	border-bottom: none !important;
}
a.logo {
	filter: Invert();
}
.navbar-nav,
.active a:hover {
	background: #666 !important;
}
.breadcrumb>li+li:before {
	padding: 0 5px;
	color: #777;
	margin-left: 4px;
	content: "/\00a0";
}
#playleft {
	margin: 30px;
}
#player {
	margin-top: 40px;
}
.container{
margin-bottom:20px;
}
}
