css:
@-moz-document domain(workflowy.com)
{

.content-img {
width: 75px;
height: 75px;
border:gray solid 1px;
border-radius: 5px;
    
-webkit-box-shadow: 1px 1px 3px 0px rgba(138,138,138,0.93);
-moz-box-shadow: 1px 1px 3px 0px rgba(138,138,138,0.93);
box-shadow: 1px 1px 3px 0px rgba(138,138,138,0.93);
    
background-size:cover;
background-position:50% 50%;
margin: 3px 3px 5px 3px;
    
opacity: .6;
transition: .2s ease;
}

.content-img:hover {
opacity:1;
}

DIV.content-img a {
display: block;
width: 75px;
height: 75px;
z-index: 9999;
}



DIV.content-img {
float: left;
}

DIV.notes {
    overflow: visible;
}

DIV.childrenEnd {
clear: both;
}

DIV.project {
clear: both;
}

DIV.parent {
clear: none !important;
}
}
