css:
@-moz-document url-prefix("https://m.vk.com")
{  .mhb_logo {
    padding-left: 9px;
    padding-right: 459px;
}

.mhi_logo {
    background-image: url(https://m.vk.com/images/logo.png);
}
.vk .basis__header .mhb_logo {
    -webkit-flex-shrink: 0;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    min-width: 160px;
}
.fit_box.new_form .button {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
    font-size: 12px!important;
    font-weight: 600;
    padding: 9px!important;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
.fit_box.new_form .selector, .fit_box.new_form .textfield {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    font-size: 15px;
    padding: 5px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    border-color: #caced4;
}
.fit_box.new_form .button.success {
    background-color: #efefef;
}
.button, .small_button {
    font-weight: 700;
    color: #fff;
    background-color: #6182aa;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
.vk__page_login .fit_box.new_form .button.success {
    margin-top: -20px;
}
.app_inline_item .app_name_label, .away_bottom .item_like, .away_bottom .item_repost, .body_fullscreen .away_page .away_bottom_date, .body_fullscreen .away_page .away_bottom_source, .bottom_panel, .button, .cp_attached_wrap .msh_desc, .email_post_change, .fi_fat .fi_subrow .option_label, .fi_header_light, .head_panel, .near_btn, .new_item_link, .owner_panel, .slim_header, .small_button, .small_near_btn, .sub_header, .text_panel, .zpv_close_btn {
    font-size: 0em;
}
}
