css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zamunda.net") {
  
  .outer, table, table.top_blocks > tbody > tr, tr:hover, .rate-banana-go, .wallpaperclick {
    background: url('https://www.toptal.com/designers/subtlepatterns/patterns/creampaper.png') !important;  
  }
  
  .colhead, .colhead_fix {
    background-color: #666 !important;
    background-image: none !important;
    border-radius: 0 !important;    
    border-bottom: 0 !important;  
  }
  
  tr[height="23"] td u, .colhead, .colhead_fix {
    text-decoration: none !important;
    color: #eee !important;
    text-shadow: 0 0 2px rgba(0, 0, 0, 10) !important;
  }
  
  
  a:hover, a:active {
    text-decoration: none !important;
    color: #E00 !important;
  }
  
  .buttonPro.grey, .buttonPro.black, .buttonPro.blue {
    box-shadow: none !important;
    background: none !important;
    background-color: #666 !important;
    border: 1px solid #666 !important;
    border-radius: 0 !important;
    text-decoration: none !important;
    text-shadow: 0 -0 1px rgba(0, 0, 0, 10) !important;
  }
  
  .buttonPro.grey:hover, .buttonPro.black:hover, .buttonPro.blue:hover {
    color: #eee !important;
    text-shadow: 1px 1px 4px #999 !important;
  }
  
  #search {
    height: 18px !important;
    margin-top: 0 !important;
    border: 1px solid #666 !important;
  }
  
  table.top_blocks > tbody > tr > td:first-child, table.top_blocks > tbody > tr > td:last-child, .rate-banana-go, #svalqneto_zapochva, .foot_cont, body > P, #lead_ad, div[align=right], #close_lead, .iframe, .td_newborder > td, #showlikes_div, #hide_button_likes, #floatingbox, td[style="border:none;"] > p[align=center], .alt_domain, .jcarousel-wrapper, b > h1, .wallpaperclick a, .wallpaperclick, td.logo2 > a > img {
    display: none !important;
    cursor: default !important;
  }
  
  .alt_domain {
    margin: 0 !important;
    margin-bottom: -10px !important;
    padding: 0 !important;
    position: relative !important;
  }
  
  #takehowmany, input, select, textarea {
    border: 1px solid #aaa !important;
    border-radius: 2px !important;
    background: none !important;
  }
  
  .table {
    border-bottom: 0 !important;
  }
  
  table.top_subnav > tbody > tr, .table {
    border: 1px solid #aaa !important;
    border-bottom: none !important;
  }
  
  a[target="_top"] > font {
    border-bottom: 0 !important;
    color: #e00 !important;
  }
  
  b {
    text-decoration: none !important;
  }
  
  .fa {
    color: #666 !important;
    font-size: 14px !important;
    text-shadow: 1px 1px 1px #ccc !important;
  }
  
  .fa:hover {
    color: #e00 !important;
  }
  
  img[height="20"], .navigation > a > img {
    height: 14px !important;
    filter: saturate(40%) !important;
  }
  
  .navigation > a > img {
    height: 20px !important;
    filter: saturate(40%) !important;
    margin-bottom: 2px !important;
  }
  
  .navigation {
    padding-bottom: 0 !important;
  }
  
}
