css:
@-moz-document domain("forum.virtualracing.org") {
html {
    background: #000 none;
}

.body_wrapper {
    background: #000 none;
}

#pagetitle h1 {
    color: #FFF;
    font: normal normal bold 14px Tahoma,Calibri,Verdana,Geneva,sans-serif;
}

#pagetitle .description {
    color: #FFF;
    font-size: 11.05px;
}

.breadcrumb .navbit.lastnavbit span {
    color: #FFF;
}
.above_body {
    background: #000 none repeat;
}
.below_body {
    color: #FFF;
}
.footer_time {
    color: inherit;
}
.below_body .time {
    color: inherit;
}
.footer_copyright {
    color: inherit;
}
.footer_copyright, .footer_morecopyright {
      color: inherit;
}
.notifications a.popupctrl:link, .notifications a.popupctrl:visited, .toplinks a:link, .toplinks a:visited {
    color: #fff;
}
.toplinks ul.isuser li.welcomelink a {
    color: #FFF;
}
.toplinks ul.isuser li a {
    color: #FFF;
}
.breadcrumb .navbit a {
    color: #FFF;
}
#pagetitle a {
    color: #FFF;
}
.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
    color: #FFF;
}
}
