@-moz-document domain("youtube.com") {
/* CSS by Jerry Hui. */


/* Hidden items: Homepage recommendation; sidebar recommendation on a video's page; end-of-video suggested videos  */
#feed, #watch7-sidebar, .ytp-endscreen-content { display:none; }

/* Change the color of the search bar to remind you this style is turned on */
#masthead-search-term { background: #e6e6e6 !important; }
}
