css:
@-moz-document url-prefix("https://www.southparkstudios.com/") {
:root{
--Greyish-Color: rgba(60,70, 50, 0.2);
--BG-Image: url(https://image.ibb.co/dp4QhF/concrete_2564978_1920.jpg) ;
--Random-Image: url(http://cdn.jsdelivr.net/emojione/assets/png/1f3b2.png?v=2.2.5);
}

html, div.article > a:nth-child(1) > div:nth-child(2), .item, [data-display-name="About"], .media, [href="/random-episode"] {
background-color: rgba(40, 40, 40, .03) !important;
}
.section-header,
 div.full-ep > a:nth-child(1) > div:nth-child(2) > div:nth-child(5) > span:nth-child(1),
div.article > a:nth-child(1) > div:nth-child(2) > div:nth-child(3) > span:nth-child(1) {
    color: #b9b9b9 !important;
}
div.full-ep > a:nth-child(1) > div:nth-child(2) > div:nth-child(1):not(.spr-header), 
div.article > a:nth-child(1) > div:nth-child(2) > div:nth-child(1), h2, h3, 
.deck > span, .header>span, .sub-header > span {
    color: #797979 !important;
}
	
.page-content {
background-image: var(--BG-Image);
background-repeat: round;
}
	
	
section.page-content.home > div > section.module.special, section.content > footer .footer, section#main_content > footer  {
display: none;
}
}
