css:
@-moz-document domain("player.adcdn.tv") {
.vjs-control-bar > .fa-fast-backward {
	order: 15;
	width: 30px;
	display: none;
}
.vjs-control-bar > .fa-fast-backward::before, .vjs-control-bar > .fa-fast-forward::before {
	font-size: 1.2em;
	line-height: 40px;
}
.vjs-control-bar > .vjs-play-control {
	order: 0;
}
.vjs-control-bar > .fa-fast-forward {
	order: 16;
	width: 30px;
	display: none;
}
.vjs-control-bar > .vjs-volume-menu-button {
	order: 1;
}
.vjs-control-bar > .vjs-current-time {
	order: 2;
}
.vjs-control-bar > .vjs-time-divider {
	order: 3;
}
.vjs-control-bar > .vjs-duration {
	order: 4;
}
.vjs-control-bar > .vjs-progress-control {
	order: 5;
}
.vjs-control-bar > .vjs-live-control {
	order: 6;
}
.vjs-control-bar > .vjs-remaining-time {
	order: 7;
}
.vjs-control-bar > .vjs-spacer {
	order: 8;
}
.vjs-control-bar > .vjs-playback-rate {
	order: 9;
}
.vjs-control-bar > .vjs-chapters-button {
	order: 10;
}
.vjs-control-bar > .vjs-descriptions-button {
	order: 11;
}
.vjs-control-bar > .vjs-subtitles-button {
	order: 12;
}
.vjs-control-bar > .vjs-captions-button {
	order: 13;
}
.vjs-control-bar > .vjs-audio-button {
	order: 14;
}
.vjs-control-bar > .vjs-fullscreen-control {
	order: 17;
}
.main-preview-player .playlist-container {
	display: none !important;
}
.vjs-big-play-button {
	left: 50% !important;
}
}
