css:
@-moz-document domain(vk.com) 
{
/*
Author - KiLleR
https://vk.com/killer_nsk
*/

/*** Global tweaks ***/

/*** Строка-заголовок (Общее растяжение)***/
#page_header 
  {
  width: 1530px !important;
  font-size: 15px;
  }
/*** Юзверь-нейм в шапке блока ***/
.head_nav 
  {
  margin-right: -67.5px;
  }
/*** Общее наполнение (Общее растяжение)***/
#page_layout 
  {
  width: 1550px !important;
  }
/*** Основная новостная лента и правый блок (исключая левое меню) ***/
#page_body 
  {
  width: 1376px !important;
  font-family: Terminal;
  }
/***Левая панель навигации***/
#side_bar .left_label, body
  {
  font-size: 15px;
  font-family: Terminal;
  }
/*** Основная новостная лента (ширина и размер основного текста постов) ***/
#wide_column 
  {
  width: 1190px;
  margin-right: -50px;
  font-size: 15px;
    }
/*** Заголовок или имя в чате ***/
.im-page--title-main
  {
  font-size: 15px;
  }
/*** Основная новостная лента (размер шрифта пост-автор) ***/
.post_author, h5.copy_post_author, h5.post_author, .author, .im-mess-stack .im-mess-stack--content .im-mess-stack--pname
  {
  font-size: 15px;
  }
/*** Основная новостная лента (размер шрифта времени создания после пост-автора) ***/
.post_date
  {
  font-size: 13.5px;
  }
/*** Основная новостная лента (размер шрифта собственного коммента) ***/
.fakeinput, div[contenteditable=true], input.big_text, input.file, input.search, input.text, input[type=button], input[type=password], input[type=search], input[type=submit], input[type=text], input[type~=email], input[type~=password], input[type~=search], input[type~=text], textarea, wall_reply_text, .wall_reply_text
  {
  font-size: 15px;
    }
/*** Правый блок ***/
#content 
  {
  width: 1390px;
  font-size: 15px;
  }
/*** Отступ между основной лентой и правым блоком  ***/
.narrow_column_wrap
  {
  margin-right: -44px;
  }
/*** Левая панель *наверх* ***/
#stl_bg 
  {
  width: 70px;
  }
#stl_text {
  font-size: 14px;
  margin-left: -12px !important;
  height: 0px;
  }
/*** Public ***/
/* Cover support*/
/*** Ширина строчки-подзаголовка в шапке группы или сообщества***/
#public > div.page_block
  {
  width: 1434px !important;
  }
/*** Высота изображения в шапке группы или сообщества***/
.page_block > .page_cover
  {
  width: 1434px !important;
  height: 350px !important;
  }
/*** Расположение кнопки подписаться (вы подписаны)***/
.page_cover_actions
{
  width: 150px !important;
  }
#wide_column.wide_column 
  {
  margin-right: -42px;
  }
/*** Photos tweaks ***/
#photos_albums_block.page_block 
  {
  width: 1434px;
  }
#photos_all_block.page_block 
  {
  width: 1434px;
  }
/*** Messages tweaks ***/
/*** Общая ширина ***/
.im-page 
  {
  width: 1434px;
  }
/*** Ширина чего-то не помню чего, но пусть будет ***/
.im-right-menu.ui_rmenu 
  {
  margin-left: 930px;
  }
/*** Список уже созданных диалогов справа ***/
.im-page .im-page--dialogs
  {
  float: right !important;
  right: 0px;
  width: 400px;
  }
/*** Имена в списке созданных диалогов справа ы***/
.nim-dialog .nim-dialog--name .nim-dialog--name-w
  {
  font-size: 13.5px;  
  color: #000000;
  font-weight: bold;
  }
.nim-dialog .nim-dialog--preview, .nim-dialog .nim-dialog--text-preview
  {
  font-size: 13.5px;
  }
/*** Создание нового диалога справа ***/
.im-page .im-create
  {
  left: auto;
  right: 0px;
  width: 400px;
  }
/*** Ширина основного поля диалога ***/
.im-page .im-page--history 
  {
  margin-left: 0px !important;
  margin-right: 401px;
  }
/*** Ширина поля ввода ответа ***/
.im-chat-input .im-chat-input--textarea 
  {
  width: 920px;
  }
/*** Videos tweaks ***/
.videocat_row 
  {
  width: 950px;
  }
.video_item 
  {
  width: 220px;
  }
.video_item .video_item_thumb 
  {
  width: 220px;
  }
.videocat_inline_player a, .videocat_featured_big 
  {
  width: 655px;
  }
.videocat_featured_playlists 
  {
  width: 930px !important;
  }
.videocat_featured_playlist 
  {
  width: 455px;
  }
/*** Ads ***/
#ads_page_wrap3 
  {
  width: 1200px;
  margin-left: -200px;
  }
#ads_left 
  {
  width: 1200px;
  margin-left: -1200px;
  }
/*** Developers docs ***/
#dev_page_wrap3 
  {
  width: 1100px;
  margin-left: -100px;
  }
}
