css:
@-moz-document domain("horriblesubs.info") {
nav.navbar {
    border-radius: 0px 0px 450px 450px/40px !important;
    box-shadow: 0px 4px 20px #0e0707 !important;
}
html,
#content,
#masthead {
background-color: #2a2d30 !important;
background-image: url("/*[[xBG]]*/") !important;  
background-repeat: no-repeat !important;
background-size: cover !important;
background-attachment: fixed;
}
.latest-show-more > a {color: #ffffffe8;}
div.well:nth-child(1) > iframe {filter: invert(91%) sepia(1) brightness(2.1) hue-rotate(150deg) saturate(2); border-radius:  400px / 70px; width: 53%; min-width:53%;}
.badge.la-badge,
.post-inner-content,
#secondary > *,
h1,
h2,
h3,
.schedule-table *:not(.schedule-table):not(tbody):not(tr):not(td),
.full-schedule > a,
div.well.homepage-well *:not(.schedule-table):not(tbody):not(tr):not(td):not(h2)
.latest-show-more  {
    background-color: #b8bbbc96;
    color: #181818 !important;
    transition: 2.5s all !important;
} 
h2, .schedule-table > tbody > tr > td * {background-color: transparent !important;}
div.latest-releases > ul > li:hover,
div.latest-releases > ul > .active:hover,
.latest-show-more a:hover {
    background-color: #c8343447 !important;
    color: #343537 !important;
    transition: 0.3s all !important;
}
.latest-releases-res > div:hover, .xMarked:hover {
  background-color: #c8343447 !important;
  transition: 0.3s all !important; 
  border-radius: 10px 334px 10px 1px !important;}
.latest-releases .active a:hover, .latest-releases a:hover {
    background: rgba(0, 0, 0, 0) !important; 
    border-radius: 10px 134px 35px 8px !important; 
    transition: border-radius .3s ease-in-out !important;
}
.latest-releases .active {
    background: #c7c7c752;
}
.latest-search-bar.form-control
 {
    background-color: #26282a !important;
    color: #333 !important;
}
div.latest-releases ul li a{color: #000 !important; font-weight: normal !important;}
#disqus_thread {filter: invert(88%)}
.latest-sponsor,
.homepage-sponsor,
.head-sponsor, 
.latest-spons,
.homepage-spons {
    display: none !important;
}
.navbar, .full-schedule, .announcement-table, .announcement-post-title > a, .schedule-table, 
.schedule-table > tbody:nth-child(1) > tr, [href="/release-schedule/"], [href="/category/announcement/"] { 
background-color: transparent !important;
text-shadow: #e6e6e6cf 0px 0px 18px;
border-bottom: none !important;
color: #151515 !important;
}
.hs-shows > div > div, .hs-shows > div > a {background-color: #86868678 !important; color: #363636 !important; padding-left: 10px; padding-right: 10px}
}

@-moz-document url-prefix("http://example.com") {

}
