css:
@-moz-document domain("open.kattis.com") {
.source-code-editor, #editor_resize{
  background-color: #111 !important;
}
.source-code-editor .ace_type, .source-code-editor .ace_keyword:not(.ace_operator){
  font-style: italic !important;
  color: #888 !important;
}
.source-code-editor .ace_content *{
  color: #888 !important; 
}
.source-code-editor .ace_numeric, .source-code-editor .ace_string{
  color: #ccc !important; 
}
.source-code-editor .ace_indent-guide{
  background: #111 !important;
  border-right: 1px #222 solid;
}
.source-code-editor .ace_comment{
  color: #723fdb !important;
}
.source-code-editor .ace_active-line{
  background-color: #222 !important;
}
.source-code-editor .ace_gutter{
  background-color: #111 !important;
}
.source-code-editor .ace_gutter *{
  color: #444 !important;
}
.source-code-editor .ace_gutter-active-line{
  background: #222 !important;
}
.source-code-editor div.ace_scrollbar::-webkit-scrollbar {
  width: 8px;
  height:8px;
}
.source-code-editor div.ace_scrollbar::-webkit-scrollbar-thumb {
    background: #333;
}
}
