css:
@-moz-document domain("open.kattis.com") {
.source-code-editor *{
  color: #707070 !important;
}
.source-code-editor .ace_type, .source-code-editor .ace_keyword:not(.ace_operator){
  font-style: italic !important;
}
.source-code-editor .ace_numeric,.source-code-editor .ace_string{
  color: #202020 !important;
}
.source-code-editor .ace_comment{
  color: #009fc4 !important;
}
.ace_gutter-active-line{
  background: #ccc !important;
}
.source-code-editor div.ace_scrollbar::-webkit-scrollbar {
  width: 8px;
  height:8px;
}
.source-code-editor div.ace_scrollbar::-webkit-scrollbar-thumb {
    background: #ccc;
}
}
