css:
@-moz-document domain("ffxiv-beta.lokyst.net"), domain("ermad.github.io"), domain("ryan20340.github.io") {
body {
    background-color: #222;
    color: #DEDEDE;
}
footer {
    background-color: #333;
    color: #DEDEDE;
}

/* Crafting window sim */
.simulator-status table {
    background-color: #333;
}

/* Initial Guess + Solver Result */
.well {
    background-color: #333;
}
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.nav-tabs > .active > a:focus {
    background-color: #222;
    color: #DEDEDE;
}

/* Probalistic/Monte Carlo breakdown */
pre {
    background-color: #333;
    color: #DEDEDE;
}

/* Macro */
textarea[readonly] {
    background-color: #333;
    color: #DEDEDE;
}
legend {
    color: #DEDEDE;
}

/* Options Menu */
.modal {
    background-color: #222;
    color: #DEDEDE;
}
.modal-footer {
    background-color: #333;
    border-top: 1px solid #000;
}

/* Updated */
.synth-status {
    background: #333;
}
.bonus-stats {
    background: #333;
}
}
