css:
@-moz-document domain("www.save-video.com") {
body {
	background-color: #222 !important;
}
.sv-hseparator {
	border-top: 1px solid #444 !important;
}
.sv-download-container {
	background-color: #333 !important;
	border: 1px solid #555 !important;
}
.sv-hsep {
	color: #bdc3ca;
	background-color: #bdc3ca !important;
	border-top: 1px solid #555 !important;
}
.sv-video-thumb-container {
	background-color: #222 !important;
	border: 1px solid #555 !important;
}
.sv-video-thumb-container img {
	background-color: #222 !important;
	border: 1px solid #555 !important;
}
.sv-download-button {
	background-color: #222 !important;
	border: 1px solid #555 !important;
	background: -moz-linear-gradient(top, #222 0%, #111 99%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #222), color-stop(99%, #111)) !important;
	background: -webkit-linear-gradient(top, #222 0%, #111 99%) !important;
	background: -o-linear-gradient(top, #222 0%, #111 99%) !important;
	background: -ms-linear-gradient(top, #222 0%, #111 99%) !important;
	background: linear-gradient(to bottom, #222 0%, #111 99%) !important;
}
.sv-searchbox-container {
	background-color: #222
}
.sv-searchbox {
	border: 2px solid #111;
	background-color: #333
}
.sv-provider-select-container {
	border: 2px solid #111;
	background-color: #333
}
.sv-provider-select-vsep {
	background-color: transparent;
}
.sv-provider-select {
	margin-top: 2px;
	margin-left: -1px;
	color: #8C8C8C;
}
.sv-download-btn-img img {
	filter: invert(80%);
}
.sv-download-btn-content {
	color: #8C8C8C;
}
.sv-home-ads {
	display: none;
}
}
