css:
@-moz-document domain('www.pogdesign.co.uk')
{

.day .info .lastep:hover,
.day .info .lastep:focus,
.day .info .lastep:active,
.day .info .vfirstep:hover,
.day .info .vfirstep:focus,
.day .infochecked .firstep,
.day .info .vfirstep:active,
.day .lastep,
.day .vfirstep,
.day .firstep
{
	box-shadow: none;
}

.month_name,
.day
{
	background-image: none;
}

.today .firstep .first
{
	color: rgba(102, 248, 132, 1);
}

.today .lastep .last
{
	color: rgba(200, 50, 50, 1);
}

.today .vfirstep p:first-of-type a:first-of-type
{
	color: rgba(250, 220, 100, 1);
}

.today,
.lightnotice
{
	background-color: rgba(243, 172, 30, 0.80);
	background-image: none;
}

}
