css:
@-moz-document domain("beta.nightbot.tv") {
.gray-bg {
    background-color: #45596C;
}

.navbar {
    background-color: #1E282E;
}

.border-bottom {
    border-bottom-color: #2F4050 !important;
}

.footer,
.breadcrumb,
.ibox-content,
.ibox-title {
    background-color: #2F4050;
    border-color: #2F4050;
    color: #e8e9eb;
}

/* This is the little black bar on the white page style, needs to be the same as the navbar*/
#page-wrapper > div:nth-child(1) > div:nth-child(1) {
    border-bottom-color: #1E282E !important;
}

.dropdown-menu {
    background-color: #1e282e;
}

body {
    color: #e8e9eb;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #0d3b58;
}

.form-control,
.single-line {
    /*background-color: #2f4050;*/
    background-color: #45596C;
}

.table-bordered>thead>tr>th,
.table-bordered>thead>tr>td {
    background-color: #1e282e;
}


.pagination {
    color: black;
}

.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    color: #fff;
    cursor: not-allowed;
    background-color: #1e282e;
    border-color: #ddd;
}

.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    background-color: #1e282e;
    color: #fff;
}

.pagination>li>a, .pagination>li>span {
    color: #fff;
    background-color: #1e282e;
}

a {
    color: #87c4f9;
    text-decoration: none;
}

a:hover {
    color: #fff;
}

.table-hover>tbody>tr:hover {
    background-color: #1e282e;
}

.btn-white {
    color: white;
    background: #1e282e;
    border: 1px solid #e7eaec;
}

.btn-white:hover, .btn-white:focus, .btn-white:active, .btn-white.active, .open .dropdown-toggle.btn-white {
    color: black;
    background: #fff;
}

.modal-content {
    background-color: #1e282e;
}

code {
    background-color: #000;
    color: #1d89cf;
}

.help-block {
    color: white;
}

.page-heading {
   background-color: #2F4050;
}

.autodj-controls {
    background-color: #1e282e;
}
}

