css:
@-moz-document domain("itc.ua") {
/*=== Scrollbar ===*/
::-webkit-scrollbar {
        background: #404040;
}

::-webkit-scrollbar-thumb {
    background: #555;
}

::-webkit-scrollbar-corner {
    background: #000;
}

/*=== Site background color ===*/
body, #wrapper > .container {
    background: #474747;
    color: #eeeeee;
}

/*=== Submenu under site header ===*/
#menu-featured-temyi {
    background: #484848;
}

/*=== Font color ===*/
a, #header .menu-tags a, .entry-content blockquote, #sidebar .block h3, .entry-header, .entry-title, body.error404 .entry-header .entry-title, body.singular .entry-header .entry-title, .entry-excerpt, #sidebar .comments, .intro1, .articles-meta .r .txt {
    color: #eeeeee !important;
}

/*=== Sidebar blocks and hotline price widget appearance===*/
#sidebar .block, #sidebar .post+.post, .itc-related-posts .post, .itc-related-posts .post-title, .hotline-linker {
    border-color: #505050;
    background: #525252 !important;
    color: #eeeeee !important;
}

/*=== Color of dividers on mine page ===*/
#content .post.block-in-loop + .post.block-in-loop, #content .home-themes-wrp, #content .home-themes-wrp + .post.block-in-loop {
    border-color: #505050;
}
    
/*=== Crousel with photos ===*/
.fotorama, .table-striped>tbody>tr:nth-of-type(odd), .table-bordered>thead>tr>th { 
    background-color: #525252;
}

/*=== Hotline TTX Table  ===*/
.table-bordered, .table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border-color: #525252;
}

.table-hover>tbody>tr:hover {
    background-color: #484848;
}

/*=== Facts with links in the end of article===*/
.post-facts {
    border-top: solid 1px #525252;
    border-bottom: solid 1px #525252;
}

body.singular .entry-content .post-facts a {
    background: transparent !important; /*This one fixes ugly code of site designers*/
}
}
