css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("amordoce.com"), domain("amoursucre.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com") {
/* Esses são os dominios que o style vai pegar, não mexer */
  
/* Fundo da página */   
  
index > .connected {
background-color : #FFFFFF !important;
background-image : url("http://www.stickdecor.com.br/wp-content/uploads/2017/05/papel-de-parede-adesivos-teens-music-te-026-3.jpg") !important ;
background-repeat : no-repeat !important ;
background-position : center center !important ;
background-attachment : fixed !important ;
}

/* Logo do Amor Doce*/
#site-logo > a > img[src*="/logo-as.png"] {
height : 0 !important ;
width : 0 !important ;
background: url("http://i65.tinypic.com/2vluqec.jpg") repeat !important ;
padding-left : 427px !important ;
padding-top : 142px !important ;   
}

.halo {background-image : url("http://www.amoursucre.com/image/halo.png") !important ;}

/* Info do jogador*/
panel-connected {background : #CDCDC1 !important ;}
#pseudo-container > .blue {
background : #FF0000 !important ;
box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
}
.title-container a {
color : #FFFFFF !important ;
text-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
}

#episode-container {background :#9B30FF !important ;}
#episode-container span {color : #FFFFFF !important ;}
#episode-container .number {color : #FFFFFF !important ;}
  
.trombone {background-image : url("http://www.amoursucre.com/image/paper-clip/left.png") !important ;}

#currencies-container .values {
background :#FFD700 !important ;
border : 2px dotted #AFEEEE !important ;
color :#FFFFFF !important ;
}

#panel-menu a {
background : #006400 !important ;
color :#FFFFFF !important ;
}
#panel-menu a:hover {
background :#7FFF00 !important ;
color : #FFFFFF !important ;
}

navigation-menu > div > .pink, navigation-menu > div > .blue {
background : #7FFF00 !important ;
color : #FFFFFF !important ;
    }
navigation-menu > div > .active, navigation-menu > div > a:hover {
background : #006400 !important ;
color : #FFFFFF !important ;
    }
navigation-menu > div > .border {
border : none !important ;
    }
side-links > .link-entry {
background :#1E90FF !important ;
    }
}
