css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Merge tabs and navigation bar - https://userstyles.org/styles/146845/ */

/* Adjust tab bar to get space first */
#TabsToolbar
{
  margin-left: 31vw !important;/* Left margin for nav bar */
  margin-right: 2em !important; /* Right margin for caption button, menu */
}

/* Move nav bar to top left */
#nav-bar
{
  margin-top: -2em !important; /* Move nav bar up */
  margin-right: 70vw !important; /* Cut it off with defined width */
  background-color: transparent !important; /* Make it use tab bar colors */;
}

/* Move menu button to top right */
#PanelUI-button
{
  position: fixed; /* Fixed position for menu */
  top: 0.2em; /* Add some margin to top */
  right: 0em; /* Move it to right side (adjust for caption buttons) */;
}

/* ---------- End of style 146845 ---------- */
