css:
@-moz-document domain("preceptit.freshdesk.com") {
/*removed guff*/

.footer-content-centre, #reminders
{
    display: none !important;
}
/*Move leader board up a step to replace reminders whitespace*/
/*Background grey*/

#inner .leftcontent, .pagearea, .no-helpdesk, .tkt-details-sticky, .index-page-content, .widget-wrapper, .widget-wrapper .widget, .header, .widget-wrapper .widget-inner, .activityfeed-wrap, .widget-wrapper .widget-inner #load-more, .widget-wrapper .widget-inner .widget-more-link, .widget-wrapper .widget-inner #load-more, .nav-tabs.sleek-tab>.active>a, .nav-tabs.sleek-tab>.active>a:hover, .toolbar, #tickets-expanded td
{
    background: #444;
}
/*Light gray bottom border for ticket list*/

#tickets-expanded td
{
    border-bottom: 1px solid #EEE;
}
/*Ticket text light gray*/

#tickets-expanded .name .info-data .emphasize, #tickets-expanded .name-user .info-data .emphasize
{
    color: #888;
}
/*White, txt etc*/

.ticket-leftcol h4.title, h5, .ticket-leftcol .leftFilter .item, .view_filters .link-item .nav-trigger, #ticket-display-id
{
    color: #fff;
}
/*precept Orange*/

.select2-container-multi .select2-choices, .widget-wrapper .widget-activity .widget-title
{
    border: 1px solid #ff8c17;
}

a, a:hover, .activityfeed, .widget-wrapper .widget-inner #load-more
{
    color: #ff8c17;
    text-decoration: none;
    text-shadow: 0px 0px;
}

.activityfeed li
{
    border-bottom: 1px dotted rgba(239, 239, 239, 0);
}

#tickets-expanded .name h3 a:hover, #tickets-expanded .name-user h3 a:hover, #tickets-expanded .name h3 a, #tickets-expanded .name-user h3 a, #tickets-expanded td a
{
    color: #ff8c17;
}
}
