css:
@-moz-document domain("wrc.t-mobile.com") {
body {
        background: #224;
        color: #dedede;
    }
    main {
        background: #222
    }
    .sidebar {
        background: #444;
        color: #dedede;
    }
    .sidebar-search {
        background: #444;
        color: #000;
    }
    .sidebar-search > .input-group {
        background: #000;
    }
    .sidebar-search-input {
        background: #000;
        color: #dedede;
    }
    .nav-tabs {
        background: #444;
        border-color: #444;
    }
    .nav-tabs .list-item {
        background: #333;
        border-color: #444;
        color: #ccc;
    }
    .nav-tabs .list-item.active {
        background: #666;
        color: #fdd;
    }
    .pills .list-item.active {
        background: #444;
        color: #fdd;
    }
    .h4,
    .h5,
    .h6,
    .secondary-heading,
    h4,
    h5,
    h6 {
        color: #ddd;
    }
    a.thread,
    a.thread:link {
        color: #bbb;
    }
    .thread.unread {
        background-color: #545
    }
    .theirs>.message {
        background: #000;
        border-color: #707;
    }
    .mine>.message {
        background: #000;
        border-color: #070;
    }
    .theirs>.message::after,
    .theirs>.message::before {
        border-top: 5px solid #707;
        border-right: 5px solid #707;
        border-bottom: 5px solid transparent;
        border-left: 5px solid transparent;
        box-sizing: border-box;
        content: "";
        height: 10px;
        position: absolute;
        top: -1px;
        left: -10px;
        width: 10px;
    }
    .mine>.message::after,
    .mine>.message::before {
        border-top: 5px solid #070;
        border-left: 5px solid #070;
        border-bottom: 5px solid transparent;
        border-right: 5px solid transparent;
        box-sizing: border-box;
        content: "";
        height: 10px;
        position: absolute;
        top: -1px;
        right: -10px;
        width: 10px;
    }
    .component>header {
        background: #444
    }
    .component-header-main::after {
        background: linear-gradient(90deg, rgba(255, 255, 255, 0), #444);
        content: "";
        height: 100%;
        position: absolute;
        right: 0;
        top: 0;
        width: 20%;
    }
    .component>section {
        background: #333;
        box-sizing: border-box;
        padding: .75rem .75rem;
    }
    footer {
        background-color: #666;
    }
    footer .textarea {
        background: #666;
        color: #fff;
    }
}
